import icons from "../Assets/img/icons.svg";
import React, { useState } from "react";

const Pagination = (props) => {
  const numPages = 10;

  const [curPage, setCurPage] = useState(1);
  const submitCountHandler = (event) => {
    event.preventDefault();
    console.log("count= " + event.currentTarget.value);
    props.pageCountHandler(event.currentTarget.value);
  };

  const submitPageIncrementHandler = (event) => {
    event.preventDefault();
    setCurPage(curPage + 1);
    //props.pageNumberCountHandler(event.currentTarget.value);
  };

  const submitPageDecrementHandler = (event) => {
    event.preventDefault();
    setCurPage(curPage - 1);
    //props.pageNumberCountHandler(event.currentTarget.value);
  };

  return (
    <div>
      <button onClick={submitCountHandler} value={10}>
        10
      </button>
      <button onClick={submitCountHandler} value={20}>
        20
      </button>
      <button onClick={submitCountHandler} value={50}>
        50
      </button>
      {curPage === 1 && numPages > 1 && (
        <button onClick={submitPageIncrementHandler} value={curPage + 1}>
          Page {curPage + 1}
        </button>
      )}
      {curPage === numPages && numPages > 1 && (
        <button onClick={submitPageDecrementHandler} value={curPage + 1}>
          Page {curPage - 1}
        </button>
      )}
      {curPage < numPages && (
          <button onClick={submitPageHandler} value={curPage + 1}>
            Page {curPage - 1}
          </button>
        ) && (
          <button onClick={submitPageHandler} value={curPage + 1}>
            Page {curPage - 1}
          </button>
        )}
    </div>
  );
};

export default Pagination;
