import React, { useEffect, useState } from "react";
import PokemonThumbnail from "./Components/PokemonThumbnail";
import Pagination from "./Components/Pagination";

function App() {
  const [pokemonsPerPage, setPokemonsPerPage] = useState(20);
  const [allPokemons, setAllPokemons] = useState([]);
  const [loadPoke, setLoadPoke] = useState(
    `https://pokeapi.co/api/v2/pokemon?limit=${pokemonsPerPage}`
  );
  const getAllPokemons = async () => {
    setAllPokemons([]);
    const res = await fetch(
      `https://pokeapi.co/api/v2/pokemon?limit=${pokemonsPerPage}`
    );
    const data = await res.json();
    //setLoadPoke(data.next);

    function createPokemonObject(result) {
      result.forEach(async (pokemon) => {
        const res = await fetch(
          `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
        );
        const data = await res.json();
        setAllPokemons((currentList) => [...currentList, data]);
      });
    }
    createPokemonObject(data.results);
    await console.log(allPokemons);
  };
  const pageCountSubmitHandler = (count) => {
    console.log("inside pageCountSubmitHandler " + count);
    setPokemonsPerPage(count);
  };

  const pageNumberCountSubmitHandler = async () => {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon");
    const data = await res.json();
    console.log("total count =" + data.count);
  };
  useEffect(() => {
    getAllPokemons();
  }, [pokemonsPerPage]);

  return (
    <div className="app-container">
      <h1>Pokemon Kingdom .</h1>
      <Pagination
        pageCountHandler={pageCountSubmitHandler}
        pageNumberCountHandler={pageNumberCountSubmitHandler}
      />

      <div className="pokemon-container">
        <div className="all-container">
          {allPokemons.map((pokemon, index) => (
            <PokemonThumbnail
              id={pokemon.id}
              name={pokemon.name}
              image={pokemon.sprites.other.dream_world.front_default}
              type={pokemon.types[0].type.name}
              key={index}
              height={pokemon.height}
              weight={pokemon.weight}
              stat1={pokemon.stats[0].stat.name}
              stat2={pokemon.stats[1].stat.name}
              stat3={pokemon.stats[2].stat.name}
              stat4={pokemon.stats[3].stat.name}
              stat5={pokemon.stats[4].stat.name}
              stat6={pokemon.stats[5].stat.name}
              bs1={pokemon.stats[0].base_stat}
              bs2={pokemon.stats[1].base_stat}
              bs3={pokemon.stats[2].base_stat}
              bs4={pokemon.stats[3].base_stat}
              bs5={pokemon.stats[4].base_stat}
              bs6={pokemon.stats[5].base_stat}
            />
          ))}
        </div>
        <button className="load-more" onClick={() => getAllPokemons()}>
          More Pokemons
        </button>
      </div>
    </div>
  );
}

export default App;
